class Tokusei {
    constructor(name, phazes) {
        this.name = name; // 特性の名前
        this.phazes = phazes; // 特性の効果の発動関数のマップ
    }

    // 特性の効果を発動するメソッド
    activate(attacker, defender, phaze) {
        if (this.phazes[phaze]) {
            this.phazes[phaze](attacker, defender);
        } else {
            console.error(`Unknown phaze: ${phaze}`);
        }
    }
}
